const amqp = require("amqplib");
const nodemailer = require("nodemailer");
require("dotenv").config();

const rabbitUrl = "amqp://localhost";
let channel;

// RabbitMQ bağlantısı
async function connectRabbitMQ() {
  const connection = await amqp.connect(rabbitUrl);
  channel = await connection.createChannel();
  await channel.assertQueue("notification_queue");

  // notification_queue'yu dinle
  channel.consume("notification_queue", async (msg) => {
    const booking = JSON.parse(msg.content.toString());
    console.log(`Sending booking confirmation for bookingId: ${booking.id}`);

    // SMTP ayarları
    const transporter = nodemailer.createTransport({
      host: process.env.SMTP_HOST,
      port: process.env.SMTP_PORT,
      secure: process.env.SMTP_PORT === "465", // SSL için true
      auth: {
        user: process.env.SMTP_USER,
        pass: process.env.SMTP_PASS,
      },
    });

    try {
      await transporter.sendMail({
        from: process.env.SMTP_USER,
        to: booking.user,
        subject: "Booking Confirmation",
        text: `Your booking is confirmed. Booking ID: ${booking.id}`,
      });
      console.log(`Email sent successfully to ${booking.user}`);
    } catch (error) {
      console.error("Error sending email:", error);
    }

    channel.ack(msg); // Mesajı işleme alındı olarak işaretle
  });
}

connectRabbitMQ();
